<script>
var loadEditorJS;

loadEditorJS = function() {
    var $productBoxWidget;

    $productBoxWidget = $('.js-productBoxWidget_slider');
    if ($productBoxWidget.length <= 0) {
        return false;
    }
    $productBoxWidget.each(function() {
        var sliderWidth,
            sliderItemWidth,
            sliderColsRwd,
            sliderColsArr,
            sliderWidthDiv,
            sliderCols,
            sliderArrows,
            sliderPagin,
            sliderShow,
            sliderHalf,
            $thisSlider,
            $sliderList,
            i;

        $thisSlider = $(this);
        sliderCols = $thisSlider.data('slider-cols');
        sliderArrows = $thisSlider.data('slider-arrows');
        sliderPagin = $thisSlider.data('slider-pagin');
        sliderShow = $thisSlider.data('slider-show');
        sliderHalf = $thisSlider.data('slider-half');

        /* slider breakpoint calculation */
        sliderWidth = $thisSlider.width();
        if (sliderWidth < 1080) {
            sliderWidth = 1080;
        }
        //sliderItemWidth = $thisSlider.find('.js-gridItem').eq(0).outerWidth();
        sliderItemWidth = 25;
        sliderItemWidth = sliderItemWidth * sliderWidth / 100;
        sliderColsArr = {};
        if (sliderItemWidth < 150) {
            sliderItemWidth = 150;
        }
        sliderColsRwd = sliderCols;
        for (i = sliderWidth; i >= sliderItemWidth; i -= 100) {
            sliderWidthDiv = Math.floor(i / sliderItemWidth);
            if (sliderWidthDiv < 1) {
                sliderWidthDiv = 1;
            }
            if (sliderWidthDiv < sliderColsRwd) {
                sliderColsRwd = sliderWidthDiv;
                sliderColsArr[i] = {
                    items: sliderColsRwd
                }
            }
        }
        /* end slider breakpoint calculation */

        $sliderList = $thisSlider.find('.js-productBoxWidget_slider_list');
        if (!$sliderList.hasClass('.owl-carousel')) {
            var columns = $thisSlider.attr('data-slider-cols');
            if (typeof columns === 'undefined') {
                columns = 4;
            }
            $sliderList.owlCarousel({
                dots: false,
                nav: false,
                navText: ['<i class="icon-arrow-left2"></i>', '<i class="icon-arrow-right2"></i>'],
                items: columns,
                slideBy: 1,
                navRewind: false,
                activeClass: 'slick-active',
                responsive: {
                    980: {
                        items: columns
                    },
                    768: {
                        items: 3
                    },
                    480: {
                        items: 2
                    },
                    400: {
                        items: 1
                    },
                    340: {
                        items: 1,
                        startPosition: 0,
                    },
                    300: {
                        stagePadding: 60,
                        items: 1,
                        startPosition: 1,
                        nav: false
                    }
                }
            });
        }
    });
};
</script>

