                        <script type="text/javascript">
$(function() {
  var form=$('#formLogin');
  var komunikat=$('#alert',form);

  function komunik(t){
    if(t)
      komunikat.html(t).slideDown('slow');
    else
      komunikat.html('').slideUp('slow');
  }

  function checkInput(n,v){
    if(v) {
      $('#'+n,form).addClass('e');
      $('#alert_'+n,form).html(v).slideDown('slow');
    } else {
      $('#'+n,form).removeClass('e');
      $('#alert_'+n,form).html('').slideUp('slow');
    }
  }

  form.submit(function(){
    $.ajax({
      url: '/ajax/uzytkownik-login/',
      data: form.serialize(),
      beforeSend: function(){
        $('input',form).attr('readonly',true);
        form.fadeTo('fast',0.3);
      },
      error: function(){
        form.fadeTo('slow',1);
        $('input',form).attr('readonly',false);
        komunik('Wystąpił błąd, <br />spróbuj ponowić');
      },
      success: function(dane){
        $('input,textarea',form).attr('readonly',false);
        if(dane.valid){
          location.reload();
        }else{
          form.fadeTo('slow',1);
          komunik(dane.alert);
          checkInput('login',dane.login);
          checkInput('password',dane.password);
        }
      }
    });
    return false;
  });
});
</script>

