    <script type="text/javascript">
        var genericSubmitForm = new VarienForm('generic-submit-form');

        genericSubmitForm.submit = function(url, updateCart) {
            if (typeof updateCart === 'undefined') {
                updateCart = false;
            }

            jQuery('#html-loader-div').show();
            
            jQuery.ajax({
                type: "POST",
                url: url,
                data: jQuery("#generic-submit-form").serialize(),
                beforeSend: function(){

                },
                success: function(data)
                {
                    
                    if(data[0].trim() === '' ){
                        console.log('Whitespace detected! Check your scripts.')                     }
                    
                    var json = JSON.parse(data);
                    if (json.status == 'SUCCESS') {
                        if (typeof json.dynamic_html !== 'undefined') {
                            jQuery('#top-basket-container').html(json.dynamic_html.mini_cart);
                        }
                        CDP.fClearClasses();
                        CDP.fProductStatuses(json.cheat_sheet);

                        if (jQuery('a[href="'+url+'"]').hasClass('wishlist')) {

                        } else {
                            CDP.fQuantityValueAnimate();

                            if (url.indexOf("checkout/cart/add") > -1) {

                                var productId = url.substring(url.lastIndexOf("product/") + 8, url.lastIndexOf("/ajax"));
                                if (isNaN(productId) === false) {
                                    CDP.faddToCartAnalytics(productId);
                                }
                            }
                            
                        }
                    } else if (json.status == "ACTION_PROMPT") {
                        jQuery.colorbox({
                            html: '<div class="alert-message">' +
                                '<p>' + json['action_prompt']['message'] + '</p>' +
                                '<form action="' + json['action_prompt']['action_url'] + '" method="POST">' +
                                    '<input type="hidden" name="form_key" value="' + json['action_prompt']['form_key'] +'"/>' +
                                    '<button class="button button-std smaller" type="submit">Tak</button>' +
                                    '<a class="button button-std smaller" onclick="jQuery.colorbox.close(); return false;">Nie</a>' +
                                '</form>' +
                            '</div>'
                        });
                    } else {
                        jQuery.colorbox({
                            html:"<div class='alert-message'><p>Wystąpił błąd!<br/>"+json.messages+"</p></div>"
                        });
                    }

                    if (!updateCart) {
                        jQuery('#html-loader-div').hide();
                    } else {
                        cart.updateCart();
                    }

                    customerProducts = json.cheat_sheet;

                },
                error: function()
                {
                    jQuery.colorbox({
                        html:"<div class='alert-message'><p>Wystąpił błąd!</p></div>"
                    });
                    jQuery('#html-loader-div').hide();
                },
                complete: function()
                {

                }

            });

            return false;
        };


