<script type="text/javascript">
    jQuery(document).on('click', "#products-grid-container .single-product a:not(.add-to-cart-container a)", function (e) { // category product title & image
        var product = jQuery(this).parents('.single-product').data('product');
        var href = jQuery(this).attr('href');
        eCommerceProductClick(e, product, 'Category', href);
    });

    jQuery(document).on('click', "#main_menu .single-product a:not(.add-to-cart-container a)", function (e) { // CDP Poleca title & image
        var product = jQuery(this).parents('.single-product').data('product');
        var href = jQuery(this).attr('href');
        eCommerceProductClick(e, product, 'Menu', href);
    });

    jQuery(document).on('click', "#slabs-container .product a:not(.add-to-cart-container a, a.close)", function (e) { // homepage
        var product = jQuery(this).parents('.product').data('product');
        var href = jQuery(this).attr('href');
        eCommerceProductClick(e, product, 'Kafelki', href);
    });

    jQuery(document).on('click', "#bestsellers .single-product a:not(.add-to-cart-container a)", function (e) { // homepage
        var product = jQuery(this).parents('.single-product').data('product');
        var href = jQuery(this).attr('href');
        eCommerceProductClick(e, product, 'Bestsellers', href);
    });

    jQuery(document).on('click', ".might-interest-you .single-product a:not(.add-to-cart-container a)", function (e) { // quartic
        var product = jQuery(this).parents('.single-product').data('product');
        var href = jQuery(this).attr('href');
        eCommerceProductClick(e, product, 'Quartic', href);
    });

    function eCommerceProductClick(e, product, source, href) {
        if (jQuery.isArray(product) && typeof dataLayer !== 'undefined') {
            e.preventDefault();

            var title = jQuery(document).find('title').text();
            title = title.replace(/(\|[^|]+)$/i, '');
            title = title.trim();

            dataLayer.push({
                'event': 'productClick',
                'ecommerce': {
                    'click': {
                        'actionField': {'list': title},
                        'products': [{
                            'name': product[1],
                            'id': product[0],
                            'price': product[2],
                            'category': title
                        }]
                    }
                }
            });

            setTimeout(function () {
                document.location = href;
            }, 600);
        }
    }
</script><script type="text/javascript">
    jQuery(document).on('click', "a.in-cart.showed, #top-basket-container a.add-to-cart-container", function (e) { // go to cart
        var cartHref = jQuery(this).attr('href');
        onDataLayerCheckoutStepCart(e, cartHref);
    });

    function onDataLayerCheckoutStepCart(e, cartHref) {
        if (typeof dataLayer !== 'undefined') {
            e.preventDefault();

            dataLayer.push({
                'event': 'checkout',
                'ecommerce': {
                    'checkout': {
                        'actionField': {'step': 1},
                        'products': []
                    }
                }
            });

            setTimeout(function () {
                document.location = cartHref;
            }, 600);
        }
    }
</script>
<script type="text/javascript">
    jQuery(document).on('click', '.deal-box.bigger-box, .deal-box.half-width-deal, .deal-box.full-width-deal, #slabs-mt .title a, #slabs-mt .check-now a, #slabs-mt .image a', function (e) {

        var banner = jQuery(this).parents('li').data('banner');
        var href = jQuery(this).find('a').attr('href');
        if (typeof href === 'undefined') { // for slabs
            href = jQuery(this).attr('href');
