        if (jQuery.isArray(banner) && typeof dataLayer !== 'undefined') {
            e.preventDefault();

            dataLayer.push({
                'event': 'promotionClick',
                'ecommerce': {
                    'promoClick': {
                        'promotions': [
                            {
                                'id': banner[0],
                                'name': banner[1],
                                'creative': banner[2],
                                'position': banner[3]
                            }
                        ]
                    }
                }
            });

            setTimeout(function () {
                document.location = href;
            }, 600);

        } else {
            document.location = href;
        }
    });
