/*slicebox_347*/
$(function() {

        var Page = (function() {
          $.Slicebox.defaults.onBeforeChange = function(pos) {
            if(!this.items || $(this.items[0]).parent().is(':visible')) {
              nav.removeClass('nav-dot-current');
              nav.eq(pos).addClass('nav-dot-current');
            }
          }

          var navArrows = $('#nav-arrows-347').hide(),
          navOptions = $('#nav-options-347').hide(),
          navDots = $('#nav-dots').hide(),
          nav = navDots.find('button'),
          shadow = $('#shadow').hide(),
          clonedEl = [],
          index = 0,
          initSlicebox = function() {
			$('#sb-slider-347').find('li').each(function(){
			  var _this = $(this),
				alt = _this.find('.sb-description').text();
			  _this.find('a').attr('title', '');
			  _this.find('img').attr('alt', alt);
			  _this.find('.sb-description').remove();
			});
            if(clonedEl.length === 0) {
              clonedEl = $('#sb-slider-347').clone();
              clonedEl.hide();
              $('#sb-slider-347').hide();
            }
            $('.cloneSlicebox').remove();
            var tmpEl = clonedEl.clone().show();
            index++;
            tmpEl.attr('id', '#sb-slider-347-' + index);
            tmpEl.addClass('cloneSlicebox');

            tmpEl.insertAfter($('.tx-slicebox h2'));



            return tmpEl.slicebox( {
              onReady : function() {

                $(nav[0]).addClass('nav-dot-current');

                navArrows.show();
                navOptions.show();
                shadow.show();
                navDots.show();
              },
              disperseFactor : 30,
              cuboidsCount : 9,
              sequentialFactor : 70,
              speed : 600,
              interval : 6000,
              colorHiddenSides : '#7fa7af',
              autoplay : true
            })
          },
          slicebox = initSlicebox(),
          init = function() {
            initEvents();
          },
          initEvents = function() {
            slicebox.isPlaying = true;
            // add navigation events
            navArrows.children(':first').on('click', function() {
              slicebox.previous();
              return false;
            });

            navArrows.children(':last').on('click', function() {
              slicebox.next();
              return false;
            });

            $('#navPlay-347').on('click', function() {
              slicebox.play();
              return false;
            });

            $('#navPause-347').on('click', function() {
              slicebox.pause();
              return false;
            });

            $('.tx-slicebox').on('click', '.sb-current a', function() {
              slicebox.pause();
            });

            nav.each(function(i) {
              $(this).on('click', function( event ) {
                var dot = $(this);

                if(!slicebox.isActive()) {
                  nav.removeClass('nav-dot-current');
                  dot.addClass('nav-dot-current');
                }

                slicebox.jump(i + 1);
                return false;
              });
            });

          };

          return { init : init, initSlicebox : initSlicebox};

				})();

				Page.init();

