        if(visible == true) {
            var $this = $(this);

            var currName = '';
            var currType = '';
            var currProdID = $('#projector_product_hidden').val() || '';
            ajax_hotspots.each(function(){
                var currHs = $(this);

                currName += currHs.attr('id')+',';
                currType += currHs.data('pagetype')+',';
            });
            currName = currName.replace(/,$/, '') || '';
            currType = currType.replace(/,$/, '') || '';

            $.ajax({
                url: '/ajax/hotspots.php?action=get-hotspots-html&zones-names='+currName+'&components='+currName+'&zones-types='+currType+'&product='+currProdID,
                dataType: 'json',
                async: false,
                beforeSend: function(){
                    ajax_hotspots.addClass('load-content loaded');
                },
                success: function(json){

                    ajax_hotspots.removeClass('load-content');

                    $.each(json, function(key, value){
                        if(value){
                            var currTarget = $('#'+key);

                            currTarget.replaceWith(value);

                            currTarget = $('#'+key);

                            currTarget.find('.big_label, .big_label_m').each(function(){
                                var $this = $(this);
                                var $last_child = $this.find(':last-child')
                                var $element_to_wrap = $last_child.size() ? $last_child : $this;
                                $element_to_wrap.wrapInner('<span class="big_label_sub"></span>')
                            }) // przebudowa labelsow

                            var mySlider = false;
                            mySlider ? mySlider.reloadSlider() : mySlider_init(4,4,400,50);


                            $('.align_row').iai_align();
                        } else
                            $('#'+key).remove();
                    });

                    if(callback)
                        callback();
                },
                error: function(){
                    ajax_hotspots.removeClass('load-content');
                    ajax_hotspots.remove();
                }
            });

            $this.unbind('inview');
        }
    });
};</script><style>

