        <script type="text/javascript">
            function resizeLayout() {
                $('#left-menu').height('auto');
                $('#right-panel').height('auto');
                $('#content').height('auto');

                var layoutHeights = [
                    $('#left-menu').height(),
                    $('#right-panel').height(),
                    $('#content').height()
                ];
                layoutHeights.sort(function(a, b) {
		    return b - a;
		});

                var highest = layoutHeights.shift();
                $('#left-menu').height(highest);
                $('#right-panel').height(highest);
                $('#content').height(highest);
            };
            $(document).ready(function(){
                resizeLayout();
            });

            $(window).on("load",function(){
                resizeLayout();
            });

        </script>
