<script>
    (function () {
        var isWidgetExpanded = false;
        var wasPhoneInputInvalid = false;
        var wasCheckboxInvalid = false;
        var areExtendedAgreemendsExpanded = false;
        var wasBoxOpenEventDispatched = false;
        var wasFormSentEventDispatched = false;
        var widgetExpansionTimerId;

        var screenType = {
            form: document.querySelector('.notus-widget__screen--form'),
            success: document.querySelector('.notus-widget__screen--success'),
            error: document.querySelector('.notus-widget__screen--error'),
            loading: document.querySelector('.notus-widget__screen--loading')
        };

        window.screenType = screenType;

        var currentlyActiveScreen = screenType.form;

        var widgetEl = document.querySelector('.notus-widget');
        var checkboxEl = document.querySelector('.notus-widget__agreement');
        var externalAgreementLinkEl = document.querySelector('.notus-widget__agreement-label a');
        var firstAgreementRadioTrueEl = document.querySelector('#notus-agreement-1-true');
        var secondAgreementRadioTrueEl = document.querySelector('#notus-agreement-2-true');
        var phoneEl = document.querySelector('#notus-widget-phone');
        var phoneWrapperEl = document.querySelector('.notus-widget__input-wrapper');
        var phoneValidationMessageEl = document.querySelector('.notus-widget__phone-input-error span');
        var retryButtonEl = document.querySelector('.notus-widget__button--retry');

        widgetEl.addEventListener('mouseenter', function () {
            if (widgetExpansionTimerId) {
                clearTimeout(widgetExpansionTimerId);
                widgetExpansionTimerId = null;
            }

            if (isWidgetExpanded) return;

            if (!wasBoxOpenEventDispatched && 'dataLayer' in window) {
                dataLayer.push({ 'event': 'notusOtwarty' });
                wasBoxOpenEventDispatched = true;
            }

            widgetEl.classList.add('expanded');
            isWidgetExpanded = true;
            document.body.addEventListener('click', bodyClickHandler);
        });

        widgetEl.addEventListener('mouseleave', function (e) {
            if (!isWidgetExpanded) return;

            widgetExpansionTimerId = setTimeout(closeWidget, 1000);
        });

        widgetEl.addEventListener('submit', function (e) {
            e.preventDefault();
            if (!isFormValid()) return;

            return sendForm();
        });

        phoneEl.addEventListener('blur', function () {
            if (!wasPhoneInputInvalid && phoneEl.value === '') return false;

            validatePhoneInput({ shake: true });
        });

        phoneEl.addEventListener('keyup', function () {
            return wasPhoneInputInvalid && validatePhoneInput({ shake: false, onlyUpdateMessage: true });
        });

        phoneValidationMessageEl.parentNode.addEventListener('click', function () {
            phoneEl.focus();
        });

        checkboxEl.addEventListener('click', function (e) {
            e.preventDefault();
            checkboxEl.classList.toggle('checked');

            if (wasCheckboxInvalid && checkboxEl.classList.contains('checked')) {
                checkboxEl.classList.remove('invalid');
            }
        });

        externalAgreementLinkEl.addEventListener('click', function (e) {
            e.stopPropagation();
        });

        retryButtonEl.addEventListener('click', function () {
            return sendForm();
        });

        function bodyClickHandler(e) {
            if ($(e.target).parents('.notus-widget').length) return;
            clearTimeout(widgetExpansionTimerId);
            closeWidget();
        }

        function closeWidget() {
            widgetEl.classList.remove('expanded');
            isWidgetExpanded = false;
            widgetExpansionTimerId = null;
            document.body.removeEventListener('click', bodyClickHandler);
        }

        function validatePhoneInput(opts) {
            var shouldTriggerShakeAnimation = (opts && opts.shake) || false;
            var shouldOnlyChangeMessage = (opts && opts.onlyUpdateMessage) || false;

            if (!shouldOnlyChangeMessage) {
                phoneWrapperEl.classList.remove('invalid');
                phoneWrapperEl.classList.remove('shake');
            }

            if (!window.IsValidPhone(phoneEl.value)) {
                phoneValidationMessageEl.parentNode.classList.add('visible');

                if (phoneEl.value.length === 0) {
                    phoneValidationMessageEl.innerText = 'To pole jest wymagane';
                } else {
                    phoneValidationMessageEl.innerText = 'Niepoprawny numer telefonu';
                }

                if (wasPhoneInputInvalid && !shouldOnlyChangeMessage) {
                    setTimeout(function () {
                        phoneWrapperEl.classList.add('invalid');

                        if (shouldTriggerShakeAnimation) {
                            phoneWrapperEl.classList.add('shake');
                        }
                    }, 100);
                } else {
                    phoneWrapperEl.classList.add('invalid');

                    if (shouldTriggerShakeAnimation) {
                        phoneWrapperEl.classList.add('shake');
                    }
                }

                wasPhoneInputInvalid = true;
                return false;
            } else if (wasPhoneInputInvalid === true) {
                phoneWrapperEl.classList.remove('invalid');
                phoneWrapperEl.classList.remove('shake');
                phoneValidationMessageEl.parentNode.classList.remove('visible');
                wasPhoneInputInvalid = false;
            }

            return true;
        }

        function validateAgreementCheckbox() {
            checkboxEl.classList.remove('invalid');

            if (checkboxEl.classList.contains('checked')) {
                wasCheckboxInvalid = false;
                return true;
            }

            if (wasCheckboxInvalid) {
                setTimeout(function () {
                    checkboxEl.classList.add('invalid');
                }, 100);
            } else {
                checkboxEl.classList.add('invalid');
                wasCheckboxInvalid = true;
            }

            return false;
        }

        function isFormValid() {
            var isCheckboxChecked = validateAgreementCheckbox();
            var isPhoneValid = validatePhoneInput({ shake: true });

            return isCheckboxChecked && isPhoneValid;
        }

        function sendForm() {
            moveToScreen(screenType.loading, function () {
                var xhr = new XMLHttpRequest();
                var endpointUrl = 'https://lp.dknotus.pl/_mail/notusonline.php?';
                var data = $(widgetEl).serialize();

                xhr.open('POST', endpointUrl + data, true);
                xhr.onreadystatechange = function () {
                    if (xhr.readyState == 4) {
                        if (xhr.status == 200) {
                            moveToScreen(screenType.success);

                            if (!wasFormSentEventDispatched && 'dataLayer' in window) {
                                dataLayer.push({ 'event': 'notusWyslany' });
                                wasFormSentEventDispatched = true;
                            }
                        } else {
                            moveToScreen(screenType.error);
                        }
                    }
                }
                xhr.send();
            });
        }

        function moveToScreen(nextScreenType, cb) {
            var callback = cb || function () { };
            currentlyActiveScreen.classList.add('hidden');

            setTimeout(function () {
                nextScreenType.classList.remove('hidden');
                currentlyActiveScreen = nextScreenType;
                callback();
            }, 300);
        }
    }());
</script>
    






