						iCountItem++;
					});
			
				
					var brandBoxSlider = $(".BrandSliderBox").width();
				
					$(".BrandSliderBox").width((brandBoxSlider-1)+"px");
				
					var  eoScroll = function() {
			
						$(".kSliderItem").each(function(){
			
							var width = $(this).width();
							var newLeft = $(this).css("left");
							newLeft = parseInt(newLeft)-2;
							if(newLeft < (0-width)){
								newLeft = (width*iCountItem)-width;
							}
							$(this).css("left",newLeft+"px");
						});
					};
			
					var refreshIntervalId = setInterval(eoScroll, 30);
					
				
					$("#kSlider").hover(function(){
							clearInterval(refreshIntervalId); 
					});
						
					$("#kSlider").mouseleave(function(){
							refreshIntervalId = setInterval(eoScroll, 30);
					});
					
				};
				
				 var iTimer3  = setInterval( function(){
                  if (window.jQuery) {    
                    console.log('brand slider start');  
                    brandSliderStart();
                    clearInterval(iTimer3); 
                  }else{
                    console.log("jquery not Load :-( ");
                  }    
                },110);	
				
				
				
				
