   function getStream()
   {
       stream_diff = $(document).scrollTop() / ($(document).height() - $(window).height()); 
       
       if (stream_diff >= 0.95 && current_p != stream_page && !stream_load  && stream_power)
       {
           
          stream_load = true;  
          
