<script>
$(document).ready(function(){
	if($("#s").val()!=""){
		var ciag = $("#s").val();
		filtruj_seriale(ciag);
	}
$("#s").on("keyup",function(){
    var ciag = $(this).val();
   filtruj_seriale(ciag);
  // alert(ciag);
});
});

function filtruj_seriale(ciag){
    $(".main_item").each(function(){
        if($(this).attr("title").toLowerCase().match(ciag.toLowerCase())){
            $(this).show();
        }else{
            $(this).hide();
        }
    });
    var spl = window.location.href.split("/");
    var sub = "http://"+spl[2]+"/"+spl[3];
    $("#main_url_podglad").val(sub+"/"+ encodeURIComponent(ciag));
}
</script>
