		<script>
			$("body").on("keyup","#search_field",function(){
				var szukany = $("#search_field").val();
				if(szukany.length<3){
					$("#search_response").html("");
					return false;
				}
				$.ajax({
					type:"POST",
					url:"/ajax.php",
					data:{search_film:szukany}
				}).done(function(msg){
					$("#search_response").html(msg);
				});
				
			});
			$("body").on("focusout","#search_field",function(){
				setTimeout(function(){
					$("#search_response").html("");
				},200)
				
			});
		</script>
		
