                        $('#response_'+id).html('Wystąpił błąd.').addClass('show');
                        setTimeout(function(){
                            $('#response_'+id).removeClass('show');
                        }, 1000);
                    }else if(data == 'no'){
                        $('#response_'+id).html('Oddałeś/łaś już głos na tę fishkę.').addClass('show');
                        setTimeout(function(){
                            $('#response_'+id).removeClass('show');
                        }, 1000);
                    }else if (!isNaN(parseInt(data.n))){
                        n_score = parseInt(data.n);
                        n_p = parseInt(data.p);
                        n_m = parseInt(data.m);
                        console.log($('#score_'+id));
                        if(n_score>0){
                            $('#score_'+id).html(n_score).removeClass('m').removeClass('n').removeClass('o').addClass('o');
                        }else if(n_score == 0){
                            $('#score_'+id).html(n_score).removeClass('m').removeClass('n').removeClass('o').addClass('n');
                        }else{
                            $('#score_'+id).html(n_score).removeClass('m').removeClass('n').removeClass('o').addClass('m');
                        }
                        if(n_p>0){$('#p_'+id).html('+'+n_p);}else{$('#p_'+id).html(n_p)}
                        if(n_m>0){$('#m_'+id).html('-'+n_m);}else{$('#m_'+id).html(n_m)}
                        if(n_m==0 && n_p == 0){
                            $('#r_'+id).css('height',50+'%');
                        }else{
                            h = (n_p/(n_p+n_m)*100);
                            //$('#r_'+id).css('height',((n_p/(n_p+n_m)*100))+'%';
                            $('#r_'+id).animate({ height: h+'%'}, 1000,function() {
                                if(h >= 100){
                                    $('#r_'+id).addClass('score_bot');
                                }else{
                                    $('#r_'+id).removeClass('score_bot');
                                }
                            });

                        }
                    }

                  },
