    var showFlashMessages = function(flashes, container) {
        try {

            if (!flashes) {
                return;
            }

            container = (typeof container === 'undefined') ? '#flashes' : container;

            var messages = $.map(flashes, function (el, k) {
                return k + '#' + el;
            });

            for (var i in messages) {
                var message = messages[i].split('#');
                var type = message[0];
                var text = message[1];
                if (type == 'error') {
                    type = 'danger';
                } else if (type == 'notice') {
                    type = 'info';
                } else if (type == 'fos_user_success') {
                    type = 'success';
                }

                var flash = '<div class="alert alert-' + type + '  alert-dismissible" role="alert">' +
                    '<button type="button" class="close" data-dismiss="alert">' +
                    '<span aria-hidden="true">&times;</span>' +
                    '<span class="sr-only">Close</span>' +
                    '</button>' +
                    '<p>' + text + '</p>' +
                    '</div>';
                $(container).append(flash);
                setTimeout(function () {
                    $(container + ' .alert').hide()
                }, 10000);
            };

            document.cookie = "flashes=; expires=Thu, 01 Jan 1970 00:00:01 GMT;path=/";
        } catch (ex) {
            // console.log(ex);
        }
    };

    $(document).ready(function() {
        var jsonFlashes = $.cookie("flashes");
        var flashes = jsonFlashes ? $.parseJSON(jsonFlashes) : null;
        showFlashMessages(flashes);
    });


