		//jQuery(".ad-image-wrapper").fullScreen(false);
		if (screenfull.enabled) {
			screenfull.toggle(jQuery('#slide-gal')[0]);
		}else{
			if(jQuery('.slide-play-block').hasClass('fullgallery')){
				esc_fullscreen();
			}else{
				jQuery(document).scrollTop(0);
				jQuery('.slide-play-block').addClass("fullgallery");
				jQuery('.over_lay').css('display','block');
				jQuery('body').css('overflow','hidden');
			}
			return false;
		}
	});
	
	var esc_fullscreen = function(){
		jQuery(document).scrollTop(100);
		jQuery('.slide-play-block').removeClass("fullgallery");
		jQuery('.over_lay').css('display','none');
		jQuery('body').css('overflow','scroll');
	}
	
	jQuery(document).keyup(function(e) {
		if (e.keyCode == 27) {
			esc_fullscreen();
		}  
	});
	
	jQuery(document).on('click','.over_lay',function(){
		esc_fullscreen();
	});
});
    //]]>
</script>
