<script type="text/javascript">
    $('#archive_datepicker').datepicker({
        minDate: new Date('2009-01-02'),
        maxDate: new Date(),
        starDate: new Date(),
        onSelect: function onSelect(fd, date) {
            submitForm(date);
        }
    })

    function submitForm(date) {
        var form = document.createElement("form");
        form.method = "GET";
        form.action = "http://www.juventudrebelde.cu/busqueda";

        var tempDate = new Date(date);
        tempDate = moment(tempDate).format("DD-MM-YYYY");
        var after = document.createElement("input");

        after.value=tempDate;
        after.name="after";
        form.appendChild(after);

        document.body.appendChild(form);

        form.submit();
    }
</script>

