      <li class="basket-item">
        <a href="#">
          <img class="basket-item-img" width="40" height="40" data-bind="attr: {src: picture, title: receiptText}" />
        </a>
        <div class="basket-item-info">
          <p class="basket-item-name" data-bind="text: receiptText"></p>
          <p class="basket-item-sku"><span>SKU:</span> <span data-bind="text: article"></span></p>
          <!-- ko if: discountText -->
          <p class="basket-item-campaign discount-text" data-bind="text: discountText"></p>
          <!-- /ko -->
        </div>
        <div class="basket-item-quantity"><span data-bind="text: quantityAmount"></span> szt.</div>
        <div class="basket-item-cost">
          <p class="basket-item-price" data-bind="text: totalpriceText"></p>
        </div>
      </li>
    </ul>
    <div class="basket-footer-container">
      <div class="wss-payment-basket-shipping" data-bind="template: {name: 'wss-payment-basket-shipping-lines-template'}"></div>
            <!-- ko if: isOnlineOrder() -->
        <p class="basket-delivery small-text">Podatek VAT (uwzględniony w łącznej cenie) <span data-bind="text: vatText" ></span></p>
      <!-- /ko -->
      <p class="basket-total">Razem <span data-bind="text: totalpriceText"></span></p>
          </div>
  </div>
</script>

