<!-- Knockout template for Click & Collect stores list -->
<script type="text/html" id="wss-click-collect-stores-list-template">
  <!-- ko ifnot: new WssClickCollect().location.has_error() -->

    <!-- ko if: $root.stores_to_display() && stores_list() && stores_list().length -->
      <div class="wss-click-collect-stores-header">
        <p>
          Wybierz sklep:        </p>

        <div class="wss-click-collect-show-all-stores">
          <label>
            <input type="checkbox" name="wss-click-collect-show-all-stores" data-bind="checked: display_atp_negative_stores" />
            Pokaż najbliższe sklepy niezależnie od dostępności produktu (<span data-bind="text: total_number_of_stores"></span>)
          </label>
        </div>
      </div>

      <div class="wss-stores-locator-stores-list-input">
        <p>Użyj pola wyboru „Pokaż wszystkie sklepy”, aby ukryć/wyświetlić sklepy, w których odbiór może nie być możliwy, ale które mogą być bliżej niż sklepy, w których jest on możliwy.</p>
      </div>
    <!-- /ko -->

    <!-- ko ifnot: new WssClickCollect().location.is_loading || new WssClickCollect().order.is_loading || $root.is_loading -->
      <!-- ko ifnot: stores_list() && stores_list().length -->
        <div class="alert alert-warning">
          Niestety, nie ma sklepów z zestawem artykułów wybranych przez Ciebie przy użyciu funkcji Kliknij i odbierz. Spróbuj zmienić zestaw lub liczbę artykułów lub zmień zamówienie na sprzedaż online.        </div>
      <!-- /ko -->
    <!-- /ko -->

    <ul class="wss-click-collect-stores-list store-list" data-bind="foreach: stores_list()">
      <!-- ko if: $index() < $root.stores_to_display() -->
          <!-- ko if: $root.storesCollactability[shop_id] -->

            <!-- ko if: $root.storesCollactability[shop_id]()  == 'ok' -->
              <li>
                <p class="store-name">
                  <a
                    href="#select_store"
                    data-bind="attr: {
                      'data-shop-id': shop_id,
                      'data-shop-name': shop_name,
                      'title': shop_name,
                      'class': 'wss-click-collect-store-link wss-click-collect-store-link-' + $root.storesCollactability[shop_id]()
                    }">
                    <span
                      data-bind="text: 'JYSK&nbsp;' + shop_name, attr: {
                        'class': $root.isCurrentStore(shop_id) ? 'wss-click-collect-selected-shop shop-name' : 'shop-name'
                      }">
                    </span>
                    <!-- ko if: street -->
                      <span class="wss-click-collect-stores-address store-address" data-bind="text: street"></span>
                    <!-- /ko -->

                    <!-- ko if: house -->
                      <span class="wss-click-collect-stores-address store-address" data-bind="text: house + ','"></span>
                    <!-- /ko -->

                    <!-- ko if: zipcode -->
                      <span class="wss-click-collect-stores-address store-address" data-bind="text: zipcode"></span>
                    <!-- /ko -->

                    <!-- ko if: city -->
                      <span class="wss-click-collect-stores-address store-address"  data-bind="text: city"></span>
                    <!-- /ko -->
                    <span class="click-and-collect-count">
                      <span data-bind="text: '(' + $root.getStoreAtpText(shop_id) + ')'"></span>
                    </span>
                    <span class="click-and-collect-time">
                      <span data-bind="text: '(' + $root.getStoreCheckedTimeText(shop_id) + ')'"></span>
                    </span>
                    </a>
                </p>
              </li>
            <!-- /ko -->

            <!-- ko if: $root.storesCollactability[shop_id]() != 'ok' -->
              <li class="disabled">
                <p class="store-name">
                  <a href="#">
                    <span
                      class="wss-click-collect-store-unavailable"
                      data-bind="attr: {'data-shop-id': shop_id}">

                      <span
                        data-bind="text: 'JYSK&nbsp;' + shop_name, attr: {
                          'class': $root.isCurrentStore(shop_id) ? 'wss-click-collect-selected-shop shop-name' : 'shop-name'
                        }">
                        JYSK&nbsp;
                      </span>
                    </span>
                    <span class="store-address">
                      <!-- ko if: $root.storesCollactability[shop_id]() == 'loading' -->
                        <span class="ajax-progress">
                          <span class="throbber"></span>
                          Sprawdzanie, czy wszystkie produkty z koszyka są dostępne w sklepie...                        </span>
                      <!-- /ko -->

                      <!-- ko if: $root.storesCollactability[shop_id]() == 'not_ok' -->
                        <span class="wss-click-collect-store-not-ok">
                          <span class="store-status">
                            Nie wszystkie produkty z koszyka można odebrać w tym sklepie                          </span>
                        </span>
                      <!-- /ko -->

                      <!-- ko if: $root.storesCollactability[shop_id]() == 'error' -->
                        <span class="wss-click-collect-store-error">
                          Błąd sprawdzania dostępnych zapasów                        </span>
                      <!-- /ko -->

                      <!-- ko if: $root.isCurrentStore(shop_id) -->
                        <span class="wss-click-collect-current-store-description badge">
                          Ten sklep już jest wybrany                        </span>
                      <!-- /ko -->
                    </span>
                    <span class="click-and-collect-count none">(0)</span>
                    <span class="click-and-collect-time">
                        <span data-bind="text: '(' + $root.getStoreCheckedTimeText(shop_id) + ')'"></span>
                      </span>
                  </a>
                </p>
            <!-- /ko -->
              </li>
          <!-- /ko -->

      <!-- /ko -->
    </ul>
  <!-- /ko -->

  <!-- ko if: $root.stores_to_display() && $root.stores_to_display() < $root.stores_list().length -->
    <div class="wss_click_collect_show_more_stores">
      <!-- ko ifnot: new WssClickCollect().location.has_error() -->
        <a href="#show_more" onclick="clickCollectOrder.shop.show_more_stores(); return false;">Pokaż więcej sklepów</a>
      <!-- /ko -->
    </div>
  <!-- /ko -->
</script>

