      border: 1px solid #e0e0e0;
  }

	.calendar_class_m table, .calendar_class_m thead, .calendar_class_m tbody, .calendar_class_m th, .calendar_class_m td, .calendar_class_m tr { 
		display: block; 
	}

	.calendar_class_m td.calendarDayWeek { 
   display: none;
	}
	
	.calendar_class_m tbody td { 
		/* Behave  like a "row" */
		border: none;
		border-bottom: 1px solid #eee; 
		position: relative;
		padding-left: 55%; 
	}
	
	.calendar_class_m tbody td:before { 
		/* Now like a table header */
		position: absolute;
		/* Top/left values mimic padding */
		top: 6px;
		left: 6px;
		width: 40%; 
		padding-right: 10px; 
		white-space: nowrap;
	}
	
	/*
	Label the data
	*/
	.calendar_class_m tbody td:nth-of-type(1):before { content: "Poniedziałek"; }
	.calendar_class_m tbody td:nth-of-type(2):before { content: "Wtorek"; }
	.calendar_class_m tbody td:nth-of-type(3):before { content: "Środa"; }
	.calendar_class_m tbody td:nth-of-type(4):before { content: "Czwartek"; }
	.calendar_class_m tbody td:nth-of-type(5):before { content: "Piątek"; }
	.calendar_class_m tbody td:nth-of-type(6):before { content: "Sobota"; }
	.calendar_class_m tbody td:nth-of-type(7):before { content: "Niedziela"; }

	.calendar_class_m tbody td.today { 
		border-bottom: 1px solid #F00; 
	}

