<script type="text/javascript">

    /* for IE7 */
    /*
     $("img").load(function(){
     var article_height = $(".articleList").height();
     $("#map").css("height", article_height+"px");
     });
     */

    $(document).ready(function() {

        /*$("img").load(function(){
         var article_height = $(".articleList").height();
         $("#map").css("height", article_height+"px");
         });*/

        $(document).bind('keydown', 'Alt+Ctrl+g', function() {
            window.location.href = "/";
        });

        $(document).bind('keydown', 'Alt+Ctrl+m', function() {
            $('#accessible-menu li a').first().focus();
        });

        $(document).bind('keydown', 'Alt+Ctrl+k', function() {
            window.location.href = $('a.footer-contact').attr('href');
        });

        if ($('#jResize').length > 0) {
            $(document).bind('keydown', 'Alt+Ctrl+s', function() {
                $('li#size1 a').trigger('click');
            });
            $(document).bind('keydown', 'Alt+Ctrl+r', function() {
                $('li#size2 a').trigger('click');
            });
            $(document).bind('keydown', 'Alt+Ctrl+l', function() {
                $('li#size3 a').trigger('click');
            });
        }

        $(document).bind('keydown', 'Alt+Ctrl+n', function() {
            window.location.href = $('a.footer-site-map').attr('href');
        });

        $('a, :input').bind('blur', function() {
            $(this).removeClass('hover');
        });

        $('a,:input').bind('mouseenter', function() {
            $(this).addClass('hover');
        });

        $('a').bind('mouseleave', function() {
            $(this).removeClass('hover');
        });

        $(':input').bind('click', function() {
            if (!$(this).hasClass('hover')) {
                $(this).addClass('hover');
            }
        });

        $('a').not('.filter-item').live('click', function() {
            if (!$(this).hasClass('hover')) {
                $(this).addClass('hover');
            }
        });
        
        $('a[href^=#]').bind('mouseenter', function() {
            $(this).addClass('clear-style');            
        });
                
        $('.flex-control-nav a').bind('mouseenter', function() {
            $(this).addClass('clear-style');
        });

        function is_touch_device() {
            return 'ontouchstart' in window
                    || 'onmsgesturechange' in window;
        };

        if (!is_touch_device()) {
            $('.zph .filter-item').bind('mouseenter', function() {
                $(this).addClass('filter-hover');
            });

            $('.zph .filter-item').bind('mouseleave', function() {
                $(this).removeClass('filter-hover');
            });
        }

        $('.zph .filter-item').bind('mouseenter', function() {
            if (!$(this).parent().hasClass('outn')) {
                $(this).parent().addClass('outn');
            }
        });


        $('#accessible-menu a').bind('click', function(e) {

            var aMenuTarget = $(this).attr('href');
            $(aMenuTarget).attr('tabindex', -1).focus();

        });



        $('.vtable').vtable();

        $('.mceItemYoutube').hide().after(function() {
            return $('<iframe title="Youtube" width="560" height="315" src="http://www.youtube.com/embed/' + $(this).attr('alt') + '" frameborder="0" allowfullscreen></iframe>')
        });

        $(window).resize(function() {
            if ($(this).width() < 481) {
                var windowWidth = $(this).width();
                $('.standardContent .section img').each(function() {
                    var that = $(this);

                    if (that.width() > 350) {
                        if (that.parent('.pic').length) {
                            that.parent('.pic').css({'float': 'none', 'width': '100%', 'border': '0', 'text-align': 'center'});
                            that.css({'float': 'none', 'display': 'inline'})
                        } else {
                            that.css({'float': 'none', 'display': 'block'});
                        }
                    } else {
                        if (that.parent('.pic').length) {
                            that.css({'max-width': '100%', 'height': 'auto'}).parent('.pic').width(155);
                        }
                    }

                });
                $('.standardContent iframe').css({'width': '100%'})
            }
            if ($(this).width() < 600) {
                $('.standardParagraph table, .standardContent table').each(function() {
                    var that = $(this);
                    if (that.width() > 440 && !that.hasClass('scroll')) {
                        that.addClass('scroll');
                        that.wrap('<p></p>');
                        that.parent().css({'overflow-x': 'scroll'})
                    }
                });
            }
        });

        $(window).resize();


        $(".jStylish").sSelect();

        if ($(".selectedTxt").length > 0)
        {
            $(".selectedTxt").css("height", "22px");
        }

        $(".standardHeader2 a").css({color: "#5CAF2D"});
        $(".standardHeader2 a").hover(function() {
            $(this).animate({color: "#009A42"}, 250).dequeue();
        }, function() {
            $(this).animate({color: "#5CAF2D"}, 250).dequeue();
        });

        $(".standardButton").hover(function() {
            $(this).find(".title").animate({color: "#C6E3B6"}, 250).dequeue();
        }, function() {
            $(this).find(".title").animate({color: "#FFFFFF"}, 250).dequeue();
        });

        $(".standardButton.whiteBgn").hover(function() {
            $(this).find(".title").animate({color: "#009A42"}, 250).dequeue();
        }, function() {
            $(this).find(".title").animate({color: "#636363"}, 250).dequeue();
        });

        $('#map-polska').cssMap({
            'size': 240,
            'visibleList': false,
            'cities': false,
            'loadingText': "Ładowanie"
        });

        var regions = [
            "region1",
            "region2",
            "region3",
            "region4",
	    "region5"
        ];

        $.each(regions, function(index, value) {

            var selector = 'li[rel=' + value + ']';

            $(selector).mouseenter(function() {
                var $this = this;
                $(selector).each(function(index) {
                    if (this != $this)
                        $(this).addClass('focus');
                });
            });

            $(selector).mouseleave(function() {
                var $this = this;
                $(selector).each(function(index) {
                    if (this != $this)
                        $(this).removeClass('focus');
                });
            });

        });


  	$.datepicker.setDefaults($.datepicker.regional['pl']);
    
//     $('#birthday').datepicker({
//         defaultDate: '+1w',
//         dateFormat: 'yy-mm-dd',
//         numberOfMonths: 1,
//         changeMonth: true,
//         changeYear: true,
//         yearRange: 'c-60:c+00'
//     });   

    	$('.datepicker').datepicker({
			dateFormat: 'dd-mm-yy'
		});

    });
</script>

