            jQuery('.movable').on('mousemove', function(event) {
                var thisX = event.pageX - jQuery(this).width() / 2,
                    thisY = event.pageY - jQuery(this).height() / 2;

                jQuery('.movable').offset({
                    left: thisX,
                    top: thisY
                });
            });
        }


