  $('a').each(function(i, o){
    var url = $(o).attr('href');

    var zeroIndexStrings = ['#', '/', '?', 'javascript', 'mailto'];

    for(var i = 0; i < zeroIndexStrings.length; i++)
    {
      if(url.indexOf(zeroIndexStrings[i]) == 0)
      {
        return;
      }
    }

    if(url.indexOf(domain) != -1)
    {
      return;
    }

    $(o).addClass('js-external-link');
  });
})(jQuery);

/**
 * Function that tracks a click on an outbound link in Analytics
 * This function takes a valid URL string as an argument, and uses that URL string
 * as the event label. Setting the transport method to 'beacon' lets the hit be sent
 * using 'navigator.sendBeacon' in browser that support it.
 */
var trackOutboundLink = function(e,url) {
  if (e.ctrlKey || e.metaKey || e.target != '') {
    ga('send', 'event', 'outbound', 'click', url );
  } else {
    e.preventDefault();
    //Sprawdzenie czy GA zostal zaladowany, dopiero wtedy wysylamy event i czekamy na callback
    //Jest to konieczne bo FF blokuje GA w trybie porno przez co nie wykonuje sie callback i link przestaje dzialac
    if (window.ga && ga.loaded) {
      ga('send', 'event', 'outbound', 'click', url, {
        'transport': 'beacon',
        'hitCallback': function(){document.location = url;}
      });
    } else {
      document.location = url;
    };
  };
};

(function($){
  if ($("a.js-external-link").length) {
    $("a.js-external-link").click(function(e){
      trackOutboundLink(e,$(this).attr("href"));
    });
  };
})(jQuery);

//Wywołanie eventu gdy użytkownik pobierze jakiś plik
(function($){
  var filetypes = /\.(zip|pdf|xml|csv|doc*|xls*|ppt*|odt|ods|odp)/i;

  $('a').each(function() {
    var href = $(this).attr('href');

    if (href && href.match(filetypes)) {
      $(this).click(function() {

        var filePath = href;

        var filePathArray = filePath.split("/");
        var filename = filePathArray[filePathArray.length - 1];

        ga('send', 'event', 'Pobrania', 'Pobranie-' + filename, window.location.href);

      });
    }
  });
})(jQuery);
</script>
