    //funkcja wywołująca lazy load poprzez hover lub kliknięcie
    function customLazy(id,event,contId){

        /*
        * id - element wywołujący funkcję
        * event - rodzaj eventu wywołania (hover lub click)
        * contId - id bloczka do przeładowania zdjęć
        */

        if(event=="click"){
            $(id).click(function() {
                $(contId + " img.lazy").lazyload({
                    chainable: false,
                    bind: "event",
                    effect : "fadeIn"
                });
            });
        }
        if(event=="hover"){
            $(id).hover(function() {
                $(contId + " img.lazy").lazyload({
                    chainable: false,
                    bind: "event",
                    effect : "fadeIn"
                });
            },function(){});
        }
    };

