<script type="text/javascript">
    (function ($, document, window, undefined) {
        $(function () {

            var activities = {
                init: function () {
                    activities.$container = $('#tl-actions');
                    activities.$btnCart = $('[data-cart-trigger]');
                    activities.$btnFav = $('.js-wishlist');
                    activities.$iconCart = $('[data-cart-item]');
                    activities.$iconFav = $('[data-fav-item]');
                    activities.$iconMsg = $('[data-msg-item]');
                    activities.$squareWrapper = $('#tl-actions .tl__squares');
                    activities.$square1 = $('#tl-actions .tl__squares .tl-square--first');
                    activities.$square2 = $('#tl-actions .tl__squares .tl-square--second');
                    activities.$square3 = $('#tl-actions .tl__squares .tl-square--third');
                    activities.$square4 = $('#tl-actions .tl__squares .tl-square--fourth');

                    activities.activateDisabled();
                    activities.chainActivate();
                },

                chainActions: function () {
                    activities.$header = $('.page__header');
                    activities.$icon = $('#tl-actions li.is-active');
                    activities.$cartPos = $('[data-cart]').offset();
                    activities.$favPos = $('[data-fav]').offset();
                    activities.$msgPos = $('[data-msg]').offset();
                    activities.$docWidth = $(window).innerWidth();
                    activities.$docHeight = Math.max(window.innerHeight, document.documentElement.clientHeight);

                    if ($(window).innerWidth() > 1024) {
                        var duration = 1.1;
                    }
                    else {
                        var duration = 1.2;
                    }

                    
                    TweenLite.to(activities.$container, 0.2, { display: 'block' });
                    TweenLite.to(activities.$container, 0.15, { opacity: 1, delay: duration * 0.2 });
                    
                    TweenLite.to(activities.$square1, 0.1, { opacity: 1, delay: duration * 0.25 });
                    TweenLite.to(activities.$square2, 0.1, { opacity: 1, delay: duration * 0.3 });
                    TweenLite.to(activities.$square3, 0.1, { opacity: 1, delay: duration * 0.35 });
                    TweenLite.to(activities.$square4, 0.1, { opacity: 1, delay: duration * 0.4 });
                    
                    TweenLite.to(activities.$icon, 0.1, { opacity: 1, delay: duration * 0.5 });
                    TweenLite.to(activities.$icon, 0.1, { scale: 0.8, delay: duration * 0.6 });
                    TweenLite.to(activities.$icon, 0.1, { scale: 1.2, delay: duration * 0.7 });
                    TweenLite.to(activities.$icon, 0.1, { scale: 1, delay: duration * 0.8 });
                    
                    TweenLite.to(activities.$icon, 0.1, { scale: 0.5, delay: duration * 1 });
                    TweenLite.to(activities.$square1, 0.1, { x: '50%', delay: duration * 1 });
                    TweenLite.to(activities.$square1, 0.1, { y: '50%', delay: duration * 1 });
                    TweenLite.to(activities.$square2, 0.1, { x: '-50%', delay: duration * 1 });
                    TweenLite.to(activities.$square2, 0.1, { y: '50%', delay: duration * 1 });
                    TweenLite.to(activities.$square3, 0.1, { x: '-50%', delay: duration * 1 });
                    TweenLite.to(activities.$square3, 0.1, { y: '-50%', delay: duration * 1 });
                    TweenLite.to(activities.$square4, 0.1, { x: '50%', delay: duration * 1 });
                    TweenLite.to(activities.$square4, 0.1, { y: '-50%', delay: duration * 1 });
                    
                    TweenLite.to(activities.$container, 0.25, { backgroundColor: 'transparent', delay: duration * 1 });
                    TweenLite.to(activities.$container, 0.05, { visibility: 'hidden', delay: duration * 1.1 });

                    
                    if (activities.$header.hasClass('is-fixed')) {

                        
                        if ($(window).innerWidth() > 480) {
                            TweenLite.to(activities.$squareWrapper, 0.1, { 'transform': 'matrix(0.5, 0, 0, 0.5, -60, -205)', delay: duration * 1.1 });

                            
                            if (activities.$cartClicked) {
                                activities.$cartLeft = activities.$docWidth - activities.$cartPos.left;

                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    y: ((activities.$docHeight / 2) + 100) * -1,
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    x: ((activities.$docWidth / 2) - activities.$cartLeft - 80),
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                            }

                            
                            else if (activities.$favClicked) {
                                activities.$favLeft = activities.$docWidth - activities.$favPos.left;

                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    y: ((activities.$docHeight / 2) + 100) * -1,
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    x: ((activities.$docWidth / 2) - activities.$favLeft - 80),
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                            }

                            
                            else {
                                activities.$msgLeft = activities.$docWidth - activities.$msgPos.left;

                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    y: ((activities.$docHeight / 2) + 100) * -1,
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                                TweenLite.to(activities.$squareWrapper, 0.35, {
                                    x: ((activities.$docWidth / 2) - activities.$msgLeft - 80),
                                    delay: duration * 1.1,
                                    ease: Power1.easeNone
                                });
                            }
                        }

                        
                        else {
                            TweenLite.to(activities.$squareWrapper, 0.1, {
                                'transform': 'matrix(0.5, 0, 0, 0.5, -125, -125)',
                                delay: duration * 1.1
                            });
                            TweenLite.to(activities.$squareWrapper, 0.35, {
                                y: ((activities.$docHeight / 2) + 100) * - 1,
                                delay: duration * 1.1,
                                ease: Power1.easeNone
                            });
                            TweenLite.to(activities.$squareWrapper, 0.35, {
                                x: (activities.$docWidth / 2) - 123,
                                delay: duration * 1.1,
                                ease: Power1.easeNone
                            });
                        }

                        TweenLite.to(activities.$icon, 0.25, { scale: 0, delay: duration * 1.6 });
                        TweenLite.to(activities.$squareWrapper, 0.25, { opacity: 0, delay: duration * 1.7 });
                        TweenLite.to(activities.$container, 0, { display: 'none', delay: duration * 2, onComplete: activities.activateDisabled });
                    }

                    
                    else {
                        TweenLite.to(activities.$squareWrapper, 0.25, { opacity: 0, delay: duration * 1.3 });
                        TweenLite.to(activities.$container, 0, { display: 'none', delay: duration * 1.6, onComplete: activities.activateDisabled });
                    }
                },

                chainClearProps: function () {
                    activities.$icons = $('#tl-actions .tl__nav li');
                    TweenLite.set(activities.$container, { clearProps: 'all' });
                    TweenLite.set(activities.$square1, { clearProps: 'all' });
                    TweenLite.set(activities.$square2, { clearProps: 'all' });
                    TweenLite.set(activities.$square3, { clearProps: 'all' });
                    TweenLite.set(activities.$square4, { clearProps: 'all' });
                    TweenLite.set(activities.$icons, { clearProps: 'all' });
                    TweenLite.set(activities.$squareWrapper, { clearProps: 'all' });
                },

                disableActivated: function () {
                    activities.$btnCart.addClass('btn-disabled');
                    activities.$btnFav.addClass('btn-disabled');
                },

                activateDisabled: function () {
                    activities.$btnCart.removeClass('btn-disabled');
                    activities.$btnFav.removeClass('btn-disabled');
                },

                hideActivated: function () {
                    this.$iconCart.removeClass('is-active');
                    this.$iconFav.removeClass('is-active');
                    this.$iconMsg.removeClass('is-active');
                },

                chainActivate: function () {
                    var self = this;

                    
                    self.$btnFav.on('click', function (e) {
                        var $this = $(this);

                        if ($this.hasClass('btn-disabled') || $this.hasClass('is-checked')) {
                            return;
                        }

                        
                        $('.js-wishlist-top').removeClass('hidden');

                        self.disableActivated();
                        self.$favClicked = true;
                        self.$cartClicked = false;
                        self.hideActivated();
                        self.$iconFav.addClass('is-active');
                        self.chainActions();
                        self.chainClearProps();
                    });
                }

            };

            activities.init();
        });
    })(jQuery, document, window);
</script>



