<script type="text/javascript"> 
    function popup_fbx(sel, params) {
        var $fancyEl = $(sel);
        if(!$.fancybox.isOpen) {
            $.fancybox.open({
                margin: [0, 0, 0, 0],
                padding: 0,
                autoSize: false,
                height: 'auto',
                scrolling: 'auto',
                content: $fancyEl,
                helpers: { overlay: { locked: false } },
                beforeLoad : function() {
                    if ( typeof $fancyEl.data('fancy-max-width') !== "undefined" && parseInt($fancyEl.data('fancy-max-width')) > 0 ) {
                        this.maxWidth  = parseInt($fancyEl.data('fancy-max-width'));
                    }
                    if ( typeof $fancyEl.data('fancy-class') !== "undefined") {
                        this.wrapCSS = $fancyEl.data('fancy-class');
                    }
                },
                afterShow : function() {
                    $(".fancybox-close").attr("title", "Zamknij");
                }, 
                afterClose : function() {
                    if (params != undefined){
                        $.cookie(params.cookieName, 1, {
                            domain: params.cookieDomain,
                            path: '/', 
                            expires: params.expire
                        });
                    }
                },                                
            });
        }
    }   
    function getPopup(sel)
    {
        $.ajax({
            url: '/plugin/ExitPopup/?action=Popup',
            type: 'GET',
            dataType: 'html',
            data: {
                id_portalu: 1,
                                    dodatkowe_parametry : {"id_geografii":1}
                            },
            success: function(response) 
            {
                $(sel).removeClass("loading");
                if(response)
                    $(sel).html(response);
            }
        });
    }
    $(document).ready(function() {
        var cookie_domain = '.nocowanie.pl';
        var cookie_name = 'nocowanie_exitpopup';   
        var cookie_expire = 7;
        if ((parseInt($.cookie(cookie_name)) || 0) == 0)
        {
            getPopup($(".js-exitpopup"));
            ouibounce(
                false, {
                aggressive: true,
                timer: 0,
                callback: function() 
                {
                    popup_fbx($(".js-exitpopup > .popup"), { cookieName: cookie_name, cookieDomain: cookie_domain, expire: cookie_expire }); 
                }
            });             
        }
         
    });
</script>
    

<script type="text/javascript">
    $(document).ready(function() {
        var login_input = $('.js-login-input');
        nocowanie.tools.crossStorage.init();
        crossStorage.onConnect().then(function () {
            var id_wylogowanego_obiektu = $.cookie('wylogowany_obiekt');
            if (id_wylogowanego_obiektu){
                crossStorage.get('pokazane_popupy').then(function(pokazane_popupy){
                    if (pokazane_popupy == null)
                        var popupy = [];
                    else
                        var popupy = JSON.parse(pokazane_popupy);

                    login_input.blur();
                    $.ajax({
                        url: '/plugin/Obiekt/?action=popupPoWylogowaniu',
                        cache : false,
                        dataType: "json",
                        type : "POST",
                        data : { 
                            id_lokalizacji: id_wylogowanego_obiektu,
                            pokazane_popupy: popupy,
                        },
                        success	: function(wynik){
                            nocowanie.tools.popup.otworz({	
                                content : $(wynik.html),
                                closeTitle: 'Zamknij',
                                afterClose: function () {
                                    login_input.focus();
                                } 
                            });
                            if (wynik.reset)
                                popupy = [];
                            if ($.inArray(wynik.wersja_popupa, popupy) == -1)
                                popupy.push(wynik.wersja_popupa);
                            crossStorage.set('pokazane_popupy', JSON.stringify(popupy));                            
                        },
                    });
                });
                $.removeCookie(
                    'wylogowany_obiekt', 
                    {
                        path: "/",
                    }
                );                  
            }         
        });  
    });
</script>

	
