<script type="text/javascript">
(function() {
    addEvent(document, 'DOMContentLoaded', function() {
        var list = document.querySelectorAll('*[data-run-module="local/main.aligncolumn_item"]');
        forEach(list, function(domElement) {
            findConteners(domElement, elementFind, ['.preloadImgDiv .preloadImgDiv'], function(content, div1, div2, div3, titleBox, divDate, img) {
                setHeight(domElement, content, div1, div2, div3, titleBox, divDate, img);
            });
        });
    });

    function elementFind(domElement, query) {
        return domElement.querySelector(query);
    }

    function forEach(list, callback) {
        for (var i = 0; i < list.length; i++) {
            runCall(list[i]);
        }
        //odizolowane uruchomienie
        function runCall(itemCallback) {
            setTimeout(function() {
                callback(itemCallback);
            }, 0);
        }
    }

    function addEvent(element, event, callback) {
        if (element.addEventListener) {
            element.addEventListener(event, callback, false);
        } else {
            element.attachEvent('on' + event, callback);
        }
    }

    function findConteners(domElement, elementFind, selectorImg, callback) {
        var content = elementFind(domElement, ".content");
        var div1 = elementFind(content, ".itemLabel");
        var div2 = elementFind(content, ".mainInfos");
        var div3 = elementFind(content, ".socialMiniBox");
        var titleBox = elementFind(div2, '.title');
        var divDate = elementFind(div2, '.publishDate');
        var img;
        for (var i = 0; i < selectorImg.length; i++) {
            img = elementFind(domElement, selectorImg[i]);
            if (img !== null) {
                callback(content, div1, div2, div3, titleBox, divDate, img);
                return;
            }
        }
    }

    function setHeight(domElement, content, div1, div2, div3, titleBox, divDate, img) {
        var heightImg = getHeight(img);
        var lineHeight = getHeight(divDate);

        domElement.style.height = heightImg + "px";
        var heihtSet = getHeightSet(heightImg - getHeight(div1) - getHeight(div3));

        titleBox.style.maxHeight = heihtSet.restHeightTitle + 'px';
        div2.style.maxHeight = heihtSet.restHeight + 'px';

        function getHeightSet(restHeight) {
            var restHeight = Math.floor(restHeight / lineHeight) * lineHeight;
            var restHeightTitle = restHeight - lineHeight;
            return {
                restHeight: restHeight,
                restHeightTitle: restHeightTitle > 0 ? restHeightTitle : 0
            };
        }

        function getHeight(nodeEl) {
            if (nodeEl === null) {
                return 0;
            }
            var rect = nodeEl.getBoundingClientRect();
            return Math.floor(rect.bottom - rect.top);
        }
    }
}());
</script>

    
