function showProd(dt) {
	prodset += dt;
	if (prodset >= 8) prodset = 0;
	if (prodset < 0) prodset = 7;
	var c = '';
	var cf = '';
	for (a = 0; a < 4; ++a) {
		nr = (prodset * 4) + a;
		c += homeprods[nr][0]+',';
		cf += ','+homeprods[nr][3];
		document.getElementById('iprod'+a).src = '/img/produkty/_small/'+homeprods[nr][2];
		document.getElementById('oprod'+a).innerHTML = homeprods[nr][1];
		document.getElementById('oprod'+a).href = document.getElementById('iprod'+a).parentNode.href = homeprods[nr][4];
		if (homeprods[nr][5] > 0) {
			document.getElementById('ipromo'+a).style.display = 'block';
			elm = document.getElementById('ipromo'+a).nextSibling;
			elm.innerHTML = '<a href="/firma/promocje/fid,'+homeprods[nr][3]+',rid,'+homeprods[nr][5]+'.html" title="'+homeprods[nr][6]+'">Promocja!</a>';
		}
		else {
			document.getElementById('ipromo'+a).style.display = 'none';
			elm = document.getElementById('ipromo'+a).nextSibling;
			elm.innerHTML = '';
		}
	}
	$.ajax({type: "GET",url: "/okn_prd_stat.php?home=1&bid="+c});
	$.ajax({type: "GET",url: "/oknstat/okn_hit.php?fid=0"+cf});

}
