    });
    
      function showSlider2(){
        sync2.show();
      }

    

    var animateSpeed = 12000 ? 12000 : 12000;
    var animateSpeedText = 12000 ? 12000 : 12000;

    var transitionStyle = false
	var autoPlay = true
    
    //Sort random function
    function random(owlSelector){
      owlSelector.children().sort(function(){
        return Math.round(Math.random()) - 0.5;
      }).each(function(){
        $(this).appendTo(owlSelector);
      });
    }

    function syncPosition(el) {
      var current = this.currentItem;
      sync2
        .find('.owl-item')
        .removeClass('synced')
        .eq(current)
        .addClass('synced');

      if(sync2.data('owlCarousel') !== undefined) {
        center(current)
      }
    }
    sync1.on('click', '.owl-page', function(e) {
      e.preventDefault();
      var number = jQuery(this).data('owlPage');
      sync2.trigger('owl.goTo',number);
    });
    sync1.on('keydown','.owl-page',function(e) {
        if(e.which == 13) {
          var number = jQuery(this).data('owlPage');
          sync1.trigger('owl.goTo',number);
          sync2.trigger('owl.goTo',number);
        }
    });
    
    function center(number) {
      var sync2visible = sync2.data('owlCarousel').owl.userItems;
      var num = number;
      var found = false;
      for(var i in sync2visible) {
        if(num === sync2visible[i]) {
          var found = true;
        }
      }
      if(found === false) {
        if(num > sync2visible[sync2visible.length - 1]) {
          sync2.trigger('owl.goTo', num - sync2visible.length + 2)
        } else {
          if(num - 1 === -1) {
            num = 0;
          }
          sync2.trigger('owl.goTo', num);
        }
      } else if(num === sync2visible[sync2visible.length - 1]) {
        sync2.trigger('owl.goTo', sync2visible[1])
      } else if(num === sync2visible[0]) {
        sync2.trigger('owl.goTo', num - 1)
      }
    }
    
    
    
    if(transitionStyle == false){
      jQuery('#stop').find('.sr-only').html(TYPO3.lang.owlStart);
      jQuery('#stop').find('.icon').addClass('icon-play');
	    
    }
    else{
      jQuery('#stop').find('.sr-only').html(TYPO3.lang.owlStop);
      jQuery('#stop').find('.icon').addClass('icon-pause');
      
    }

	if(autoPlay == true){
	  changeStatus($(this));
	}
	
    jQuery('#stop').on('click',function(){
      changeStatus($(this));
    });
    
    jQuery('#stop').on('keydown',function(e) {
        if(e.which == 13) {
          changeStatus($(this));
        }
    });
   
    function changeStatus(elem){
      jQuery('#stop').toggleClass('active');

        if(transitionStyle == false){
          
          jQuery('#stop').find('.sr-only').html(TYPO3.lang.owlStop);
          jQuery('#stop').find('.icon').removeClass('icon-play');
          jQuery('#stop').find('.icon').addClass('icon-pause');
		  
          sync1.trigger('owl.play',[animateSpeed]);
          sync2.trigger('owl.play',[animateSpeedText]);
          transitionStyle = true;
        }
        else{
          jQuery('#stop').find('.sr-only').html(TYPO3.lang.owlStart);
          jQuery('#stop').find('.icon').addClass('icon-play');
          jQuery('#stop').find('.icon').removeClass('icon-pause');
		  
          sync1.trigger('owl.stop');
          sync2.trigger('owl.stop');
          transitionStyle = false;
        }
    }

    jQuery("#stop-button").prependTo(".main-owl-carousel .owl-controls").css('display','inline-block');
      
    
    
  });
  



