    (function(){
        
        if (typeof(adsLayoutIsDetal) !== 'undefined' && adsLayoutIsDetal === true  && !(typeof (adsLayoutIsSpecialTopic)!== 'undefined' && adsLayoutIsSpecialTopic === true))
            runSlot('flat-ss-detal');
