        function runSlot(slotRun) {
           
            onetAds.on(slotRun, slotRunShow);
            onetAds.addSlot(slotRun);
            onetAds.show(slotRun);
            
            function slotRunShow(slot, slotRunHtml) {
                
                var ids = getSlotsId(slotRunHtml);
                
                for (var i=0; i < ids.length; i++)
                    addEventOnSubslot(ids[i]);
                }
            
            function addEventOnSubslot(slotName){
                
                onetAds.on(slotName, showHeaderAndFooter);
                }
            
            function getSlotsId(html) {
                
                var ids = [];
                
                jQuery('<div>' + html + '</div>').find('.onet-ad').each(processItem);
                
                return ids;
                
                function processItem(key, item){
                    
                    var id       = jQuery(item).attr('id');
                    var slotName = getSlotName(id);
                    
                    if (slotName !== null)
                        ids.push(slotName);
                    }
                
                function getSlotName(id) {
                    
                    if (typeof(id) === 'string' && id.length > 8 && id.substr(0, 8) === 'onet-ad-')
                        return id.substr(8);
                    
                    return null;
                    }
                }
            
            function showHeaderAndFooter() {
                
                var extraAdsBlock = jQuery('#extraAdsBlock');

                extraAdsBlock.find('.ad_adInfo.asideTitle').show();
                extraAdsBlock.find('.ad_adInfoEnd').show();
                }
            }
        }());
