                //viia.log('Reset Slideshow');
                clearInterval(slideshow);
                play = false;
            }
        }
        var jsonSlides = '[{"title":"Robert Lewandowski: Utrzyma\u0107 form\u0119 jak najd\u0142u\u017cej","url":"\/reprezentacje\/reprezentacja-a\/aktualnosci\/2018-03-20\/robert-lewandowski-utrzymac-forme-jak-najdluzej","src":"\/public\/system\/images\/articles\/6531\/10005-home.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","mobile":"\/public\/system\/images\/articles\/6531\/10005-mobile.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","cat":"Reprezentacja A"},{"title":"STS Oficjalnym Sponsorem Reprezentacji Polski","url":"\/federacja\/aktualnosci\/2018-03-20\/sts-oficjalnym-sponsorem-reprezentacji-polski","src":"\/public\/system\/images\/articles\/6530\/10003-home.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","mobile":"\/public\/system\/images\/articles\/6530\/10003-mobile.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","cat":"Federacja"},{"title":"Jacek Magiera trenerem reprezentacji Polski do lat 20","url":"\/federacja\/aktualnosci\/2018-03-19\/jacek-magiera-trenerem-reprezentacji-polski-do-lat-20","src":"\/public\/system\/images\/articles\/6525\/9998-home.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","mobile":"\/public\/system\/images\/articles\/6525\/9998-mobile.jpg?ts=d41d8cd98f00b204e9800998ecf8427e","cat":"Federacja"}]';
       
 
    viia.log('Home Slide Init');
 
