<script>

$(function() {
if(getCookie("slideroff") != "1") {
	$(window).scroll(function(){
		var distanceTop = 400;
		if  ($(window).scrollTop() > distanceTop)
			$('#slidebox').animate({'right':'0px'},300);
		else
			$('#slidebox').stop(true).animate({'right':'-430px'},100);
	});
	$('#slidebox .closex').bind('click',function(){
		$(this).parent().remove();
	});
}
$("#never-show").click(function(e) {
	e.preventDefault();
	$("#slidebox").remove();	
	setCookie("slideroff", "1", 24*14);
});
});
</script>

