	var content = '<span><h1>Wykorzystano wyrażenie specjalne</h1>'
		+ '<p>Aby zawęzić kryteria, skorzystaj z wyszukiwarki zaawansowanej lub poniższych komend:</p>'
		+ '<ul>'
		+ '<li>Wyszukiwanie zasobów opisanych dowolnym z podanych słów: komenda OR wpisana pomiędzy słowami, np. poziomka OR malina,</li>'
		+ '<li>Wyszukiwanie konkretnego wyrażenia: zapisane w cudzysłowie, np. „czasowniki nieregularne",</li>'
		+ '<li>Wykluczenie zasobów zawierających konkretne słowo: znak „-" bezpośrednio przed słowem, np. krzyżak -pająk.</li>'
		+ '<ul></span>';


		
	var solrSpecialCharacters = [
	                         	'+','-','[',']','*',':', '"', "'", '!'
	                         	];
 	var solrSpecialStrings = [
							'OR','AND','TO','NOW'
 	                      	];
		
	$(document).ready(function() {
		var tooltipsterContainer = $('<div></div>');
		$('#query').after(tooltipsterContainer);
		
		$(tooltipsterContainer).tooltipster({
			animation : 'grow',
			speed : 250,
			trigger : 'custom',
			theme : '.tooltipster-shadow',
			onlyOne : false,
			arrow : true,
			position : 'bottom',
			offsetX : 0,
			fixedWidth : 450,
			interactive : true,
			interactiveTolerance : 99999,
			content : content
		});
		
		$('#query').keyup(function(e) {
			var found = false;
			var value = $(this).val();
			
			$.each(solrSpecialCharacters, function() {
				var regex = new RegExp('\\'+this,'i');
				if(regex.test(value)) {
					found = true;
					return false;
				}
			});
			if(!found) {
				$.each(solrSpecialStrings, function() {
					var regex =  new RegExp('([^a-z0-9]|^)'+this+'([^a-z0-9]|$)','i');
					if(regex.test(value)) {
						found = true;
						return false;
					}
				});
			}

			if(found) {
				$(tooltipsterContainer).tooltipster('show');
			} else {
				$(tooltipsterContainer).tooltipster('hide');
			}
		}).focusout(function() {
			$(tooltipsterContainer).tooltipster('hide');
		}).focusin(function() {
			$(this).trigger('keyup');
		});
	});
</script>
