			if($(this).is(':checked')) {
				$('#sid-group-'+$(this).data('code')+' .group-option input[type=checkbox]').attr('checked','checked');
			} else {
				$('#sid-group-'+$(this).data('code')+' .group-option input[type=checkbox]').removeAttr('checked');
