		});
		// obsługa reszty checkboxów
		$('#sidContainer .group-option input').change(function() {
			var group = $(this).parent().parent();
			if(group.find('.group-option input:not(:checked)').length > 0) {
				group.find('.select-all input[type=checkbox]').removeAttr('checked');
			} else {
				group.find('.select-all input[type=checkbox]').attr('checked','checked');
				
			}
		});
						
		$('.switch-to-simple').click(function(e){

