  var endpoint = 'https://surge.surge.sh/stats'

  $.getJSON(endpoint, function (data) {
    var $stats = $('.js-stats')
    $stats.find('.js-stats-deployments').text(data.formatted.deployments)
    $stats.find('.js-stats-published').text(data.formatted.bytes)
    $stats.find('.js-stats-projects').text(data.formatted.projects)
    $stats.removeClass('is-invisible')
  })

})()
</script></body></html>