				$trainings_articles_wrapper_inner.animate({left: left + 'px'}, 500 );
			}
			else if ($(this).is('.last')  && $this_id == $(this).attr('id')) {
				if(current_index < 5) {
					current_index++;
				}
				else {
					current_index = 0;
				}
				var left = current_index * (-move_index);
				$trainings_articles_wrapper_inner.animate({left: left + 'px'}, 500 );
			}
			else if($this_id == $(this).attr('id')) {
				var splitted = $this_id.split('_');
				current_index = splitted[2];
				var left = current_index * (-move_index);
				$trainings_articles_wrapper_inner.animate({left: left + 'px'}, 500 );
			}
		});

		var $trainings_articles_div = $('#trainings_articles-div-'+current_index);
		$trainings_articles_wrapper.animate({height: ($trainings_articles_div.height() + 20) + 'px'}, 500);

		$trainings_articles.removeClass('selected');
		$('.ul_trainings_articles li#za_li_' + current_index).addClass('selected');
	});
}
