<script type='text/javascript'>    
   var ktorypokazany7 = 1;       
    var ileSliderow7 = 3;        
   var done7 = true;     
   var miasto = '1'; 
   var geosuc = false; 
   function show7(op) 
   {  	 
       if (!done7)   		 
           return;       
       if(!$('#divbc-'+miasto+'-'+1)) 
           return;       
       done7 = false;      
        var dopokazania7;      
         var optionsshow7 = {};     
         var optionsshow7hide = {};     
         if(op == 'next')       
         {      
            if(ktorypokazany7 == ileSliderow7)     
                dopokazania7 = 1;     
               else       
                dopokazania7 = ktorypokazany7+1;    
            if($('#divbc-'+miasto+'-'+dopokazania7).length == 0){  
                dopokazania7 = 1; 
            }  
            optionsshow7 = { direction: 'right' };   
            optionsshow7hide = { direction: 'left' };   
        }      
       if(op == 'prev')     
        {    
            if(ktorypokazany7 == 1)    
                dopokazania7 = ileSliderow7;     
            else    
                dopokazania7 = ktorypokazany7-1;    
             if($('#divbc-'+miasto+'-'+dopokazania7).length == 0){  
                dopokazania7 = 1; 
            }  
            optionsshow7 = { direction: 'left' };   
              optionsshow7hide = { direction: 'right' };   
          }    
        $( '#divbc-'+miasto+'-'+ktorypokazany7 ).hide( 'slide', optionsshow7hide, 500, callback7w );  	   
        $( '#divbc-'+miasto+'-'+dopokazania7 ).show( 'slide', optionsshow7, 500, callback7w );    
       ktorypokazany7 = dopokazania7;     
    }      
   function callbackw() {    };      
   function callback7w() {     done7 = true;     }     
   function zmienMiasto(miato, auto) 
   {   	
        if (!done7)   		
            return; 						
	        done7 = false; 
       console.log(miasto); 
       var arg = miato; 
       if(!auto){ 
           arg =  $('#geo').find(':selected').val(); 
       } 
       if($('#divbc-'+miasto+'-'+ktorypokazany7).length == 1){  
          document.getElementById('divbc-'+miasto+'-'+ktorypokazany7).style.display='none';  
       }  
       if($('#divbc-'+arg+'-'+1)) 
           document.getElementById('divbc-'+arg+'-'+1).style.display=''; 
          miasto = arg;  
         ktorypokazany7 = 1;  
         $('#geo').val(arg);
        done7 = true; 
   }    function displayLocation(latitude,longitude){ 
       var request = new XMLHttpRequest(); 
       var method = 'GET'; 
       var url = 'http://maps.googleapis.com/maps/api/geocode/json?latlng='+latitude+','+longitude+'&sensor=true'; 
       var async = true;
        request.open(method, url, async); 
       request.onreadystatechange = function(){ 
         if(request.readyState == 4 && request.status == 200){
            var data = JSON.parse(request.responseText);
            var address = data.results[0]; 
           ustawgeo(address.formatted_address); 
           geosuc = true; 
         } 
       };
        request.send();
      }; 
     var successCallback = function(position){ 
       var x = position.coords.latitude; 
       var y = position.coords.longitude;
        displayLocation(x,y); 
     }; 
     var errorCallback = function(error){ 
           zmienMiasto(miasto, true); 
     }; 
     var options = {
       enableHighAccuracy: true, 
       timeout: 3000, 
       maximumAge: 0 
     };
    $(document).ready(function(){			
        navigator.geolocation.getCurrentPosition(successCallback,errorCallback,options);
        setTimeout(function(){ 
           if(!geosuc) 
               zmienMiasto(miasto, true);	 
       },5000); 
   });	
    function ustawgeo(pos){ 
       var n=pos.split(', ');  
       var znalezione = false; 
       for(i = 0;i<n.length;i++){ 
           $('#geo').find('option').each(function() { 
               if($(this).text() == n[i] && !znalezione) 
               { 
	                zmienMiasto($(this).val(), true); 
	                znalezione = true; 
                } 
            });
         } 
    }
   </script>  
