    <script type="text/javascript">
    $(function () {
        
        var grid;
        
        var options = {
        cell_height: 0,
		width: 160,
        vertical_margin: 0
        };
        $('.grid-stack').gridstack(options);
        grid = $('.grid-stack').data('gridstack');
		grid.disable();
		
		$(window).resize(function() {
			
			if($(window).width() > 768)
			{
				var size_w = parseInt($('.grid-stack .grid-stack-item:nth-child(1)').attr('data-gs-width'));
				var size_h = parseInt($('.grid-stack .grid-stack-item:nth-child(1)').attr('data-gs-height'));
				
				var current_width = parseInt($('.grid-stack .grid-stack-item:nth-child(1)').css('width').replace('px', ''));
							
				var new_height = ((current_width / (size_w*10)) * ((size_h)*20)) / size_h;
				
				grid.vertical_margin(new_height); 
			}
		 
		});   
		
		$('.grid-stack img').each(function() {
			 
			if(!$(this).attr('data-tmpsrc').indexOf('.jpg'))
			{
				$(this).attr('src', $(this).attr('data-tmpsrc'));
			}
			else
			{
			
				var width = parseInt($(this).parents('.grid-stack-item').css('width').replace('px', ''));
				var new_width = width + (50 - (width % 50));
							
				var mthis = this;
  
  				 $.ajax({ 
				
					type: "POST",
					url: 'functions/ajax/bannerp.php',
					data: 'file='+$(mthis).attr('data-tmpsrc')+'&width='+new_width,
					dataType: 'json',
					success: function(data) {
						
						$(mthis).attr('src', $(mthis).attr('data-tmpsrc').replace('.jpg', '-'+new_width+'b.jpg'));
						 
					}
					
				 });
				console.log(new_width);
			}
			
		});

        function showImages()
        {
        	$('.grid-stack-item').each(function() {
            
				var topp = (parseInt($(this).attr('data-gs-y')) * 10) + 100;
			
				var mst = parseInt($(window).scrollTop())+parseInt($(window).height());
			
            	if(topp < mst)
                {
					$(this).css('display', 'block');
                    $(this).find('img').attr('src', $(this).find('img').attr('data-src'));
                 }
            
            });
        }
 
		showImages();
		
		$(window).resize();
		
		$(window).scroll(function() {
			showImages();
		});
		
		
    });
    </script>
    
