function verifyEmail() {
	var email = jQuery("#email").val();
	jQuery.ajax({
		type: "POST",
		url: "http://wyzwanie90dni.pl/lp/val.php",
		data: "email=" + email,
		beforeSend: function() {
     		var over = '<div id="load" style="position: absolute;top: 50%;left: 50%;margin-top: -64px;margin-left: -64px;"></div>';
        	jQuery(over).appendTo('#contactForm');
        	jQuery('#contactForm').addClass('loading');
        	jQuery('#contactForm input').addClass('loading');
        	new imageLoader(cImageSrc, 'startAnimation()');
  		},
  		complete: function(){
     		jQuery('#load').remove();
     		jQuery('#contactForm input').removeClass('loading');
     		jQuery('#contactForm').removeClass('loading');

  		},
		success : function(text) {
			if (text == "success") {
				formSuccess();
			} else {
				formError();
				submitMSG(false,text);
			}
		}
	});
}

function formSuccess(){

	jQuery.extend({
	    redirectPost: function(location, args) {
	        var form = jQuery('<form></form>');
	        form.attr("method", "post");
	        form.attr("action", location);

	        jQuery.each( args, function( key, value ) {
	            var field = jQuery('<input></input>');

	            field.attr("type", "hidden");
	            field.attr("name", key);
	            field.attr("value", value);

	            form.append(field);
	        });
	        jQuery(form).appendTo('body').submit();
	    }
	});

	var email = jQuery("#email").val();
	var redirect = 'http://wyzwanie90dni.pl/zapis-do-programu/?source=glowna';
	jQuery.redirectPost(redirect, {'email': email});
}

function formError(){
    jQuery("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        jQuery(this).removeClass();
    });
}
function removeError() {
	jQuery('#email').click(function () {
    	jQuery("#msgSubmit").text('');
	});
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "text-center tada animated text-success";
    } else {
        var msgClasses = "text-center text-danger";
    }
    jQuery("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}
var cSpeed=6;
var cWidth=128;
var cHeight=128;
var cTotalFrames=20;
var cFrameWidth=128;
var cImageSrc='https://wyzwanie90dni.pl/lp/images/sprites.gif';

var cImageTimeout=false;
var cIndex=0;
var cXpos=0;
var SECONDS_BETWEEN_FRAMES=0;

function startAnimation(){
	
	document.getElementById('load').style.backgroundImage='url('+cImageSrc+')';
	document.getElementById('load').style.width=cWidth+'px';
	document.getElementById('load').style.height=cHeight+'px';
	
	//FPS = Math.round(100/(maxSpeed+2-speed));
	FPS = Math.round(100/cSpeed);
	SECONDS_BETWEEN_FRAMES = 1 / FPS;
	
	setTimeout('continueAnimation()', SECONDS_BETWEEN_FRAMES/1000);
	
}

function continueAnimation(){
	
	cXpos += cFrameWidth;
	//increase the index so we know which frame of our animation we are currently on
	cIndex += 1;
	 
	//if our cIndex is higher than our total number of frames, we're at the end and should restart
	if (cIndex >= cTotalFrames) {
		cXpos =0;
		cIndex=0;
	}
	
	document.getElementById('load').style.backgroundPosition=(-cXpos)+'px 0';
	
	setTimeout('continueAnimation()', SECONDS_BETWEEN_FRAMES*1000);
}

function imageLoader(s, fun)//Pre-loads the sprites image
{
	clearTimeout(cImageTimeout);
	cImageTimeout=0;
	genImage = new Image();
	genImage.onload=function (){cImageTimeout=setTimeout(fun, 0)};
	genImage.onerror=new Function('alert(\'Could not load the image\')');
	genImage.src=s;
}</script>
