function rec_extset(partner, placeholder, data)
{
    var requestData = {
        'des': data,
        'desp': partner,
        'slot': placeholder
    };

    if (placeholder == 'product_page') {
        requestData.sku_main = product.SKU;
    }

    $.ajax({
        'type': 'POST',
        'url': '/extset/slot',
        'data': requestData
    }).success(function(response) {
        if (placeholder != 'popup') {
            $('#extset_' + placeholder).html(response);
        } else {
            if (response != '') {
                if (typeof(gaTrackEvent) !== 'undefined') {
                    gaTrackEvent('externset_popup', 'emission');
                }
                autoPopupOpen(null, null, 400, 'auto', true, 0, response);
            }
        }
    });
}
